(function(){var a=window.performance.now();window.benchmark={start:a,lastCalc:a,getTotalTime:function(b){return b-this.start},getCurTime:function(b){return b-this.lastCalc},calcElapsed:function(){var c=window.performance.now(),b=[this.getCurTime(c),this.getTotalTime(c)];this.lastCalc=c;return b},outputStatus:function(){var b=this.calcElapsed();return" -> "+b[0]+" ("+b[1]+")"}}})();